const EXCLUDE_DIR_NAMES = /^\./;
const START_MARK = '<!--AUTO-->';
const PATH = 'http://www.apolis.pub'
let fs = require('fs');

scanDirAndMakeHtml('.');

function scanDirAndMakeHtml(dir) {
    let names = fs.readdirSync(dir);

    if (names.indexOf('index.html') >= 0) {
        let content = fs.readFileSync(`${dir}/index.html`, 'utf-8');
        if (content.trim().indexOf(START_MARK) != 0)
            return;
    }

    let dirs = [];

    names.forEach(name => {
        if (EXCLUDE_DIR_NAMES.test(name)) return;

        let stat = fs.statSync(`${dir}/${name}`);
        if (stat.isDirectory()) {
            dirs.push(name);
            scanDirAndMakeHtml(`${dir}/${name}`);
        }
    });

    fs.writeFileSync(`${dir}/index.html`, makeHtml(dirs, dir));
}

function makeHtml(subDirs, curDir) {
    let html = `${START_MARK}
<style>
h1 {
    margin: 1em calc(1em - 16px);
}
ul {
    line-height: 1.5;
    list-style-type: none;
}
ul li {
    position: relative;
}
ul li::before {
    content: url("data:image/png;base64,");
    position: absolute;
    left: -24px;
    top: 5px;
}
ul.parent li::before {
    content: url("data:image/png;base64,");
}
</style>
<h1>$path</h1>
$parent
<hr>
<ul>
    $children
</ul>`;

    if (curDir == '.') {
        html = html.replace('$path', `${PATH}`);
        html = html.replace('$parent', '');
    } else {
        html = html.replace('$path', `${PATH}/${curDir.replace(/^\.\//, '')}/`);
        html = html.replace('$parent', '<ul class="parent"><li><a href="../">Parent Directory</a></li></ul>');
    }
    let children = '';

    subDirs.forEach(subDir => {
        children += `<li><a href="${subDir}/">${subDir}</a></li>`
    });
    html = html.replace('$children', children);
    return html;
}